mapper.select:
SELECT user_bind.open_id,user_bind.name,user_bind.phone,user_bind.head_img FROM chat_user JOIN user_bind ON chat_user.open_id=user_bind.open_id LEFT JOIN user_tag on user_bind.id = user_tag.bind_user_id LEFT JOIN user_agent on user_agent.user_phone = user_bind.phone
chat_user.state = 0
AND (user_bind.name like '%${query.searchKey}%' OR user_bind.phone like '%${query.searchKey}%')
AND user_agent.agent = #{query.agentId}
AND user_tag.tag_id = #{query.tagId}
ORDER BY chat_user.chat_user_id DESC
