mapper.insert:
INSERT INTO user_bind(open_id,name,sex,phone,head_img,subscribe_time)
VALUES(#{openId},#{name},#{sex},#{phone},#{headImg},#{subscribeTime})
ON DUPLICATE KEY UPDATE name=#{name},sex=#{sex},phone=#{phone},head_img=#{headImg},subscribe_time=#{subscribeTime}

mapper.select:
SELECT user_bind.open_id,user_bind.phone, user_bind.subscribe_time,user_bind.head_img, user_bind.push_count,user_bind.name, user_bind.sex,tag.id tagId,tag.name tagName FROM user_bind LEFT JOIN user_tag ON user_bind.id = user_tag.bind_user_id LEFT JOIN tag ON user_tag.tag_id = tag.id
AND user_tag.tag_id in
#{tag}
AND user_bind.phone in
#{phone}
